import React, { useRef, useState, useEffect } from 'react';

function CodeBlock({ code }) {
  const codeRef = useRef(null);
  const [showCopySuccess, setShowCopySuccess] = useState(false);

  const handleCopy = () => {
    const codeElement = codeRef.current;
    if (codeElement) {
      window.getSelection().removeAllRanges();  // Clear any current selections
      const range = document.createRange();  // Create a new range
      range.selectNodeContents(codeElement);  // Select the text content of the code element
      window.getSelection().addRange(range);  // Add the range to the selection
      try {
        const successful = document.execCommand('copy');  // Execute copy command
        setShowCopySuccess(successful);  // Show success message if the copy was successful
      } catch (err) {
        console.error('Copy failed', err);  // Log the error if copy failed
      }
      window.getSelection().removeAllRanges();  // Remove selection from document
    }
  };


  useEffect(() => {
    if (showCopySuccess) {
      const timer = setTimeout(() => {
        setShowCopySuccess(false);
      }, 1500); 
      return () => clearTimeout(timer);
    }
  }, [showCopySuccess]);

  return (
    <div className="code-container">
      <pre ref={codeRef}><code>{code}</code></pre>
      <button onClick={handleCopy} className="copy-btn">Copy</button>
      {showCopySuccess && <div className="copy-success">Code copied to clipboard!</div>}
    </div>
  );
}


export default CodeBlock
